mapper.select:
SELECT device_device.id, device_device.room_id, device_device.`online`, device_device.`enable`, device_device.frameware_version, device_device.name, device_device.floor_id, device_device.product_id, family.name as familyName, family_room.`name` AS roomName, family_floor.`name` AS floorName, device_product.`name` AS productName, device_device.create_time, device_device.update_time, device_device.gateway FROM device_device inner join family on device_device.family_id=family.id INNER JOIN family_room ON family_room.id = device_device.room_id INNER JOIN family_floor ON device_device.floor_id = family_floor.id INNER JOIN device_product ON device_product.id = device_device.product_id WHERE 1 = 1
and family_floor.id=#{floorId}
and family_room.id=#{roomId}
and device_product.id=#{categoryId}
and family.id=${familyId}

SELECT device_device.id, device_device.chip_id, device_device.room_id, device_device.`online`, device_device.`enable`, device_device.frameware_version, device_device.product_id, device_device.nick_name, device_device.floor_id, device_device.create_time, device_device.update_time, ( SELECT COUNT( 1 ) FROM device_user_favorite WHERE device_user_favorite.device_id = device_device.id and device_user_favorite.user_id= #{user.id} ) AS favorite FROM device_device
INNER JOIN perm_user_device ON device_device.id = perm_user_device.device_id
WHERE device_device.room_id = #{roomId}
AND perm_user_device.user_id = #{user.id}
AND ( SELECT COUNT( 1 ) FROM device_user_favorite WHERE device_user_favorite.device_id = device_device.id and device_user_favorite.user_id= #{user.id} )=#{favorite}

SELECT device_log.create_time AS opTime, system_user.id AS userId, system_user.`name` AS userName, device_device.id AS deviceId, device_device.nick_name AS deviceName, family_floor.id AS floorId, family_floor.`name` AS floorName, family_room.id AS roomId, family_room.`name` AS roomName, device_log.payload AS controlPayload FROM device_log INNER JOIN system_user ON device_log.user_id = system_user.id INNER JOIN device_device ON device_device.id = device_log.device_id INNER JOIN family_floor ON device_device.floor_id = family_floor.id INNER JOIN family_room ON device_device.room_id = family_room.id WHERE (device_log.order_from = 'man' or device_log.order_from = 'onebutton') AND device_log.direction = 'down'
and system_user.id=#{userId}
and family_room.id=#{roomId}
and device_device.id=#{deviceId}
and device_log.create_time >= #{startTime}
and device_log.create_time <= #{endTime}
ORDER BY device_log.create_time DESC limit 50
